          if (Util.Keys.Specials.Tab < Game.Collections.DeviceState.END) {
            int   count = UI.TabList.Count;
            sbyte shift = Util.Keys.Modifiers.Shift < Game.Collections.DeviceState.END ? (sbyte) -1 : (sbyte) +1;

